import React, { Component } from "react";
import Container from "./components/Container";
import Row from "./components/Row/Row";
import Col from "./components/Col";
import Navbar from "./components/Navbar";
import Jumbotron from "./components/Jumbotron";
import mcu from "./mcu.json";
import MovieCard from "./components/MovieCard/index";

class App extends Component {
  state = {
    mcu,
    score: 0,
    highScore: 0,
    picks: mcu,
    initialState: this.state
  };


  gameOver = () => {
    this.setState(initialState)
  }

  picker = id => {
    console.log(`picker received ${id}`)
    if (this.state.mcu.some( element => element.id === id)) {
      const mcu = this.state.mcu.filter(movie => movie.id !== id)
      const picks = this.state.picks.sort(function(a, b){return 0.5 - Math.random()})
      console.log(mcu)
      this.setState({ mcu, picks }) 
    } else {
      console.log('wrong answer')
      this.gameOver()
    }

  }

  scored = () => {
    this.setState({ score: this.state.score + 1 })
    if (this.state.score > this.highScore) {
      this.setState({ highScore: this.state.score })
    }
  }

  render() {
    return (
      <>
        <Navbar score={this.state.score}
        highScore={this.state.highScore}
        />
        <Container>
          <Jumbotron />
          <Row>
            <Col>
            <Container>
              <div className="d-flex flex-wrap justify-content-center">
              {this.state.picks.map(movie => (
                <MovieCard
                scored={this.scored}
                picker={this.picker}
                id={movie.id}
                key={movie.id}
                name={movie.name}
                image={movie.image}
                />
              ))}

              }
              </div>
            </Container>
            </Col>
          </Row>
        </Container>
      </>
    );
  }
}
export default App;
